function createfigure(ymatrix1)
%CREATEFIGURE(YMATRIX1)
%  YMATRIX1:  bar matrix data

%  Auto-generated by MATLAB on 02-Feb-2016 19:10:43

% Create figure
figure1 = figure;

% Create axes
axes1 = axes('Parent',figure1,'YGrid','on',...
    'XTickLabel',{'20 words','40 words','60 words'},...
    'XTick',[1 2 3],...
    'XGrid','on',...
    'FontSize',11,...
    'FontName','Calibri');
%% Uncomment the following line to preserve the Y-limits of the axes
% ylim(axes1,[0 1]);
box(axes1,'on');
hold(axes1,'all');

% Create multiple lines using matrix input to bar
bar1 = bar(ymatrix1,'BarWidth',0.6,'Parent',axes1);
set(bar1(1),'FaceColor',[0 0 1],'DisplayName','CPWCV');
set(bar1(2),'FaceColor',[1 0 0],'DisplayName','Star Forest');
set(bar1(3),'FaceColor',[0 1 1],'DisplayName','Cycle Cover');

% Create ylabel
ylabel('Distortion','FontSize',11,'FontName','Calibri');

% Create title
title('Distortion metric','FontSize',12,'FontName','Calibri');

% Create legend
legend(axes1,'show');

