function createfigure(ymatrix1)
%CREATEFIGURE(YMATRIX1)
%  YMATRIX1:  bar matrix data

%  Auto-generated by MATLAB on 02-Feb-2016 11:10:17

% Create figure
figure1 = figure;

% Create axes
axes1 = axes('Parent',figure1,'YGrid','on',...
    'XTickLabel',{'CPWCV','Star Forest','Cycle Cover'},...
    'XTick',[1 2 3],...
    'XGrid','on',...
    'FontSize',11,...
    'FontName','Calibri');
%% Uncomment the following line to preserve the Y-limits of the axes
% ylim(axes1,[0 1]);
box(axes1,'on');
hold(axes1,'all');

% Create multiple lines using matrix input to bar
bar1 = bar(ymatrix1,'BarWidth',0.6,'Parent',axes1);
set(bar1(1),'FaceColor',[0.678431391716003 0.921568632125854 1],...
    'DisplayName','Bounding Box');
set(bar1(2),'DisplayName','Convex Hull');

% Create ylabel
ylabel('Compactness','FontSize',11,'FontName','Calibri');

% Create title
title('Space metric','FontSize',12,'FontName','Calibri');

% Create legend
legend(axes1,'show');

